function [fitresult, gof] = createSineFit(t, coefs2_linefit, startPoint)
%CREATEFIT(T,COEFS2_LINEFIT)
%  Create a fit.
%
%  Data for 'untitled fit 1' fit:
%      X Input : t
%      Y Output: coefs2_linefit
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 28-Oct-2013 15:23:42



% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( t, coefs2_linefit );

% Set up fittype and options.
ft = fittype( 'sin1' );
opts = fitoptions( ft );
opts.Display = 'Off';
opts.Lower = [-Inf 0 -Inf];
opts.StartPoint = startPoint;
opts.Upper = [Inf Inf Inf];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

    % Plot fit with data.
    figure( 'Name', 'untitled fit 1' );
    h = plot( fitresult, xData, yData );
    legend( h, 'coefs2_linefit vs. t', 'untitled fit 1', 'Location', 'NorthEast' );
    % Label axes
    xlabel( 't' );
    ylabel( 'coefs2_linefit' );
    grid on


end
